def handle_back_button(page: Page):
    if len(page.views) > 1:
        # إذا كان هناك أكثر من عرض في المكدس، ارجع للخلف
        page.views.pop()
        top_view = page.views[-1]
        page.go(top_view.route)
    else:
        # إذا كان هذا هو العرض الأخير، أغلق التطبيق
        page.window_destroy()

