# ========= فئات المساعدة ============
class AzkarHelper:
    @staticmethod
    def check_completion(azkar_list, azkar_type, page):
        if all(azkar["count"] == 0 for azkar in azkar_list):
            AzkarHelper.show_completion_message(azkar_type, page)

    @staticmethod
    def show_completion_message(azkar_type, page):
        page.views[-1].controls.clear()
    
        centered_content = Column(
            [
                Container(
                    content=Column(
                        [
                            Icon(name=Icons.CHECK_CIRCLE, size=50, color="green"),
                            Text("الحمد للَّه", size=22, color="white", weight="bold", text_align=TextAlign.CENTER),
                            Text(
                                f"لقد أتممت {azkar_type}\nتقبل الله طاعتكم إن شاء اللَّه",
                                size=18,
                                color="white",
                                text_align=TextAlign.CENTER
                            ),
                            ElevatedButton(
                                text="أظهر الشاشة الرئيسية",
                                bgcolor="ORANGE",
                                color="black",
                                on_click=lambda _: page.go("/")
                            ),
                            ElevatedButton(
                                text="شارك التطبيق لتعم الفائدة واكسب الأجر والثواب",
                                bgcolor="ORANGE",
                                color="black",
                                on_click=lambda _: print("تمت مشاركة التطبيق!")
                            )
                        ],
                        horizontal_alignment=CrossAxisAlignment.CENTER,
                        alignment=MainAxisAlignment.CENTER,
                        spacing=15
                    ),
                    padding=30,
                    width=400,
                    bgcolor="#342897",
                    border_radius=20,
                    alignment=alignment.center
                )
            ],
            alignment=MainAxisAlignment.CENTER,
            expand=True
        )
    
        page.views[-1].controls.append(centered_content)
        page.update()

    @staticmethod
    def decrement_count(e, azkar_list, azkar_type, page):
        if e.control.data["count"] > 0:
            e.control.data["count"] -= 1
            e.control.text = str(e.control.data["count"])

            if e.control.data["count"] == 0:
                e.control.bgcolor = "#3f8e9e"
            else:
                e.control.bgcolor = "blue"

            e.control.update()
            AzkarHelper.check_completion(azkar_list, azkar_type, page)
           
