class AzkarPage:
    @staticmethod
    def create(azkar_list, title, route_name, page):
        # حاوية رئيسية مع تأثيرات انتقال سلسة
        main_container = Container(
            opacity=0,
            animate_opacity=300,
            expand=True,
            content=Column(
                scroll=ScrollMode.AUTO,
                expand=True,
                horizontal_alignment=CrossAxisAlignment.CENTER,
            ),
            alignment=alignment.center,
        )

        # دالة لتحميل المحتوى بعد ظهور الصفحة
        def load_content():
            azkar_widgets = []
            for azkar in azkar_list:
                content = Column(
                    [
                        Image(
                            src=azkar["icon"],
                            width=390,
                            height=230,
                            fit=ImageFit.CONTAIN,
                            gapless_playback=True,
                        ) if "icon" in azkar else Container(),
                    
                        Container(
                            content=Text(
                                spans=AzkarHelper.format_azkar_text(azkar["text"]),
                                text_align="center"
                            ),
                            alignment=alignment.center,
                            width=400
                        ),
                    
                        ElevatedButton(
                            text=str(azkar["count"]),
                            bgcolor="blue",
                            color="white",
                            width=500,
                            height=95,
                            style=ButtonStyle(
                                shape=RoundedRectangleBorder(radius=15),
                                padding=20,
                            ),
                            on_click=lambda e, a=azkar_list, t=title: AzkarHelper.decrement_count(e, a, t, page),
                            data=azkar,
                        )
                    ],
                    spacing=2,
                    horizontal_alignment=CrossAxisAlignment.CENTER
                )
                
                azkar_widgets.append(
                    Container(
                        content=content,
                        padding=15,
                        border_radius=10,
                        bgcolor="#122536",
                        alignment=alignment.center,
                        width=400,
                        margin=margin.only(top=10)
                    )
                )

            main_container.content.controls = azkar_widgets
            main_container.opacity = 1
            page.update()

        # دالة للعودة مع تأثير إخفاء
        def navigate_back(_):
            main_container.opacity = 0
            page.update()
            time.sleep(0.3)
            page.go("/")

        def handle_back(e):
            if Page.route == route_name:
                navigate_back()
                return True
            return False

        # الطريقة المضمونة (بدون تعارض مع أحداث الأزرار)
        Page.on_back = handle_back

        app_bar = AppBar(
            title=Text(title, size=17, color=Colors.WHITE, text_align=TextAlign.CENTER),
                    bgcolor=APP_BGCOLOR,
                    center_title=True,
                    leading=IconButton(
                        icon=Icons.ARROW_BACK_IOS if page.rtl else Icons.ARROW_FORWARD,
                    on_click=navigate_back,
                ),
            
        )

        view = View(
            route_name,
            [app_bar, main_container],
            horizontal_alignment=CrossAxisAlignment.CENTER,
            bgcolor="#041a29"
        )

        def delayed_load():
            time.sleep(0.1)
            load_content()

        threading.Thread(target=delayed_load, daemon=True).start()

        return view

