class ElectronicTasbihPage:
    def __init__(self, page: Page):
        self.page = page
        self.count = 0
        self.goal = 33  # الهدف الافتراضي
        self.create_view()

    def create_view(self):
        # عناصر الواجهة
        self.counter_text = Text(
            value="0",
            size=80,
            weight=FontWeight.BOLD,
            color=Colors.BLACK,
            text_align=TextAlign.CENTER
        )
        
        self.progress_text = Text(
            value="0%",
            size=24,
            color=Colors.GREEN,
            text_align=TextAlign.CENTER
        )
        
        # زر التسبيح الكبير في المنتصف
        increment_button = Container(
            content=ElevatedButton(
                content=Column(
                    [
                        Text("تسبيح", size=20,color=Colors.WHITE,)
                    ],
                    alignment=MainAxisAlignment.CENTER,
                    spacing=5
                ),
                on_click=self.increment_counter,
                width=150,
                height=150,
                style=ButtonStyle(
                    shape=CircleBorder(),
                    bgcolor={"": "#4CAF50", "hovered": "#388E3C"},
                    padding=20,
                    animation_duration=300
                )
            ),
            margin=margin.only(bottom=20)
        )
        
        # زر التصفير الصغير تحته
        reset_button = ElevatedButton(
            content=Row(
                [Icon(Icons.REFRESH, size=20,color=Colors.WHITE,), Text("تصفير",color=Colors.WHITE, size=14)],
                alignment=MainAxisAlignment.CENTER,
                spacing=5
            ),
            on_click=self.reset_counter,
            width=100,
            height=40,
            style=ButtonStyle(
                shape=RoundedRectangleBorder(radius=20),
                bgcolor={"": "#F44336", "hovered": "#D32F2F"},
                padding=10,
                animation_duration=300
            )
        )
        
        # إنشاء الواجهة الجديدة مع تعديل شريط التطبيق ليكون متسقًا مع باقي الصفحات
        self.view = View(
            route="/electronic_tasbih",
            controls=[
                AppBar(
                    title=Text("السبحة الإلكترونية", size=17, color=Colors.WHITE, text_align=TextAlign.CENTER),
                    bgcolor=APP_BGCOLOR,
                    center_title=True,
                    leading=IconButton(
                        icon=Icons.ARROW_BACK_IOS if Page.rtl else Icons.ARROW_FORWARD,
                            icon_color=Colors.WHITE,
                            on_click=lambda _: self.page.go("/"),
                        ),
                   
                ),
                Container(
                    content=Column(
                        controls=[
                            Container(height=30),
                            
                            # العداد
                            Container(
                                content=self.counter_text,
                                padding=20,
                                bgcolor=Colors.with_opacity(0.2, Colors.BLACK),
                                border_radius=50,
                                width=200,
                                height=200,
                                alignment=alignment.center,
                                border=border.all(2, Colors.BLACK),
                                margin=margin.only(bottom=30)
                            ),
                            
                            # زر التسبيح الكبير
                            increment_button,
                            
                            # زر التصفير الصغير
                            reset_button,
                            
                            Container(height=20),
                            
                            # نسبة الإنجاز
                            self.progress_text,
                            
                            # توجيهات للمستخدم
                            Container(
                                content=Column(
                                    [
                                        Text("اضغط على زر التسبيح الكبير", size=16, color=Colors.BLACK),
                                        Text("اضغط تصفير لبدء من جديد", size=14, color=Colors.BLACK)
                                    ],
                                    spacing=5,
                                    horizontal_alignment=CrossAxisAlignment.CENTER
                                ),
                                padding=10,
                                margin=margin.only(top=20)
                            )
                        ],
                        horizontal_alignment=CrossAxisAlignment.CENTER,
                        scroll=ScrollMode.AUTO,
                        expand=True
                    ),
                    padding=padding.only(bottom=30),
                    expand=True
                )
            ],
            bgcolor="#c4c9cc",
            horizontal_alignment=CrossAxisAlignment.CENTER
        )
    
    def increment_counter(self, e):
        self.count += 1
        self.update_display()
        
        # إشعار عند الوصول للهدف
        if self.count == self.goal:
            self.page.snack_bar = SnackBar(
                content=Text(f"تهانينا! لقد أكملت {self.goal} تسبيحة", text_align=TextAlign.CENTER),
                bgcolor=Colors.GREEN,
                duration=2000
            )
            self.page.snack_bar.open = True
    
    def reset_counter(self, e):
        self.count = 0
        self.update_display()
    
    def update_display(self):
        self.counter_text.value = str(self.count)
        
        # حساب النسبة المئوية للإنجاز
        if self.goal > 0:
            progress = min(self.count / self.goal, 1.0)
            self.progress_text.value = f"{int(progress * 100)}%"
        
        self.page.update()

