def create(page):
    view = View(...)  # إنشاء العرض
    page.views.append(view)  # إضافة العرض إلى المكدس
    return view
# ============ الدالة الرئيسية ============


# تعريف قائمة لتتبع سجل التنقل
navigation_stack = []
def main(page: Page):
    # إعدادات الصفحة الأساسية
    page.title = APP_TITLE
    page.window_icon = APP_ICON
    page.window.width = APP_SIZE[0]
    page.window.height = APP_SIZE[1]
    page.window.top = APP_POSITION[1]
    page.window.left = APP_POSITION[0]
    page.bgcolor = "transparent"  # تغيير هذا السطر لجعل الخلفية شفافة
    page.window.resizable = False
    page.horizontal_alignment = "center"
    page.vertical_alignment = "center"
    page.scroll = 'auto'
    page.platform = "android"
    page.padding = padding.only(top=0)
    page.rtl = True  # للغة العربية (اتجاه من اليمين لليسار)
    page.on_keyboard_event = lambda e: handle_back_button(page) if e.key == "Escape" else None


    # إضافة الخطوط
    page.fonts = {
        FONT_NAME: FONT_FILE,
        BUTTON_FONT: FONT_FILE,
    }

    def route_change(e):
        print(f"الانتقال إلى: {e.route}, السجل قبل التحديث: {navigation_stack}")
        # تعطيل تأثير الانتقال
        page.transition = None
        
        # إضافة المسار الحالي إلى سجل التنقل (إذا لم يكن موجودًا بالفعل)
        if not navigation_stack or navigation_stack[-1] != e.route:
            navigation_stack.append(e.route)

        # تجنب تكرار تحميل نفس الصفحة
        if len(page.views) > 0 and page.views[-1].route == e.route:
            print("الصفحة موجودة بالفعل، لن يتم التحميل مرة أخرى")
            return

        # تحميل الصفحات بناءً على المسار
        if e.route == "/":
            page.views.clear()  # تنظيف كل الصفحات
            navigation_stack.clear()  # تنظيف السجل
            navigation_stack.append(e.route)
            page.views.append(HomePage.create(page))
        elif e.route == "/qibla":
            qibla_webview = QiblaWebViewPage(page)
            page.views.append(qibla_webview.create_view())
        elif e.route == "/about":
            page.views.append(AboutPage.create(page))
        elif e.route.startswith("/search/"):
            search_query = e.route.split("/search/")[1]
            page.views.append(SearchResultsPage.create(page, search_query))
        elif e.route == "/azkar_sabah":
            page.views.append(AzkarPage.create(azkar_sabah, "أذكار الصباح", "/azkar_sabah", page))
        elif e.route == "/azkar_masaa":
            page.views.append(AzkarPage.create(azkar_masaa, "أذكار المساء", "/azkar_masaa", page))
        elif e.route == "/azkar_baad_salah":
            page.views.append(AzkarPage.create(azkar_baad_salah, "أذكار بعد الصلاة", "/azkar_baad_salah", page))
        elif e.route == "/adeiat_wa_azkar_mutanawiea":
            page.views.append(AzkarPage.create(adeiat_wa_azkar_mutanawiea, "أدعية وأذكار متنوعة", "/adeiat_wa_azkar_mutanawiea", page))
        elif e.route == "/azkar_azima":
            page.views.append(AzkarPage.create(azkar_azima, "أذكار عظيمة", "/azkar_azima", page))
        elif e.route == "/alsalat_ealaa_alnabii":
            page.views.append(AzkarPage.create(alsalat_ealaa_alnabii, "الصلاة على النبي", "/alsalat_ealaa_alnabii", page))
        elif e.route == "/ayat_alkursii":
            page.views.append(AzkarPage.create(ayat_alkursii, "آية الكرسي", "/ayat_alkursii", page))
        elif e.route == "/adeiat_llmtwffy":
            page.views.append(AzkarPage.create(adeiat_llmtwffy, "أدعية للمتوفي", "/adeiat_llmtwffy", page))
        elif e.route == "/islamic_library":
            page.views.append(IslamicLibraryPage.create(page))
        elif e.route == "/learn_prayer":
            page.views.append(LearnPrayerPage.create(page))
        elif e.route == "/electronic_tasbih":
            page.views.append(ElectronicTasbihPage(page).view)
        elif e.route == "/sadaqa_gariya":
            page.views.append(AzkarPage.create(sadaqa_gariya, "صدقة جارية", "/sadaqa_gariya", page))
        elif e.route == "/sleep":
            page.views.append(AzkarPage.create(sleep, "أذكار قبل النوم", "/sleep", page))
        elif e.route == "/Nabawi_Mosque":
            page.views.append(AzkarPage.create(Nabawi_Mosque, "أدعية النبي صلى الله عليه وسلم", "/Nabawi_Mosque", page))
        elif e.route == "/prayer_times":
            page.views.append(PrayerTimesPage.create(page))
        elif e.route == "/settings":
            page.views.append(SettingsPage.create(page))
        elif e.route == "/wudu_learning":
            page.views.append(WuduLearningPage.create(page))
        elif page.route == "/timed_sunan":
            page.views.append(TimedSunanPage.create(page))
        elif page.route == "/untimed_sunan":
            page.views.append(UntimedSunanPage.create(page))

        print(f"تم تحميل الصفحة: {e.route}, السجل بعد التحديث: {navigation_stack}")
        page.update()

    page.on_route_change = route_change
    page.go("/")

app(target=main, assets_dir='assets')
